import { utils } from "ethers";
import { Select } from "antd";
import React, { useState } from "react";
import { Address, AddressInput } from "../components";
import { useTokenList } from "eth-hooks/dapps/dex";

const { Option } = Select;

const listOfTokens = useTokenList(
  "https://raw.githubusercontent.com/SetProtocol/uniswap-tokenlist/main/set.tokenlist.json",
);

export default function Lender({ 
    yourLocalBalance, 
    mainnetProvider, 
    price, 
    address,
    nftName,
    nftAddress 
}) {

  return (
      <div>
        <div style={{ border: "1px solid #cccccc", padding: 16, width: "80%", margin: "auto", marginTop: 64 }}>
            <h2 style={{fontSize: "relative", color: "green"}}>{nftName} <span style={{color: "whitesmoke"}}>NFT Loaning Terms</span></h2> 

            <div style={{ margin: 8 }}>
            <div>
              <b>useTokenList()</b> can get you an array of tokens from{" "}
              <a href="https://tokenlists.org" target="_blank" rel="noopener noreferrer">
                tokenlists.org!
              </a>
            </div>
            <Select
              showSearch
              value={selectedToken}
              onChange={value => {
                console.log(`selected ${value}`);
                setSelectedToken(value);
              }}
              filterOption={(input, option) => option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0}
              optionFilterProp="children"
            >
              {listOfTokens.map(token => (
                <Option key={token.symbol} value={token.symbol}>
                  {token.symbol}
                </Option>
              ))}
            </Select>
          </div>
        </div>
      </div>
  );
}
